  return (
    <div className="planos-container">
      <h1>Conheça nossos planos de personalização</h1>
      <div className="planos">
        <div className="plano-basic">
          <h2>Plano Basic</h2>
          <p>Mensagem e Foto</p>
          <p>Sem Flores e Catavento</p>
          <p>R$ 1,00</p>
        </div>
        <div className="plano-silver">
          <h2>Plano Silver</h2>
          <p>Mensagem e Foto</p>
          <p>Com Flores</p>
          <p>Sem Catavento</p>
          <p>R$ 50,00</p>
        </div>
        <div className="plano-gold">
          <h2>Plano Gold</h2>
          <p>Mensagem e Foto</p>
          <p>Com Flores e Catavento</p>
          <p>R$ 80,00</p>
        </div>
      </div>
    </div>
  );
}

export default ContratacaoPlanos;
