    setPlanoSelecionado(plano);
    setPrecoPlano(preco);
  }

  return (
    <div className="container">
      <h1>Conheça nossos planos de personalização</h1>
      <div className="planos-container">
        <div
          className={`plano-basic ${
            planoSelecionado === 'basic' ? 'selecionado' : ''
          }`}
          onClick={() => handleSelecionarPlano('basic', 1)}
        >
          <h2>Plano Basic</h2>
          <p>Inclui mensagem e foto</p>
        </div>
        <div
          className={`plano-silver ${
            planoSelecionado === 'silver' ? 'selecionado' : ''
          }`}
          onClick={() => handleSelecionarPlano('silver', 50)}
        >
          <h2>Plano Silver</h2>
          <p>Inclui mensagem, foto e flores</p>
        </div>
        <div
          className={`plano-gold ${
            planoSelecionado === 'gold' ? 'selecionado' : ''
          }`}
          onClick={() => handleSelecionarPlano('gold', 80)}
        >
          <h2>Plano Gold</h2>
          <p>Inclui mensagem, foto, flores e catavento</p>
        </div>
      </div>
      {planoSelecionado && (
        <div className="checkout-container">
          <h3>Checkout</h3>
