    { id: 2, nome: 'Silver', preco: 50, mensagem: true, foto: true, flores: true, catavento: false },
    { id: 3, nome: 'Gold', preco: 80, mensagem: true, foto: true, flores: true, catavento: true },
  ]);

  const handleClick = (id) => {
    setSelected(id);
  };

