            </ul>
          </div>
        ))}
      </div>
    </div>
  );*/

  const [selectedPlan, setSelectedPlan] = useState("basic");

  const handlePlanClick = (plan) => {
    setSelectedPlan(plan);
  };

  return (
    <div className="container">
      <h1>Conheça nossos planos de personalização</h1>
      <div className="planos-container">
        <div
          className={`plano-basic ${selectedPlan === "basic" ? "selected" : ""}`}
          onClick={() => handlePlanClick("basic")}
        >
          <h2>Plano Basic</h2>
          <h3>R$ 1,00</h3>
          <ul>
            <li>mensagem</li>
            <li>foto</li>
          </ul>
        </div>
        <div
          className={`plano-silver ${
            selectedPlan === "silver" ? "selected" : ""
          }`}
          onClick={() => handlePlanClick("silver")}
        >
          <h2>Plano Silver</h2>
          <h3>R$ 50,00</h3>
          <ul>
            <li>mensagem</li>
            <li>foto</li>
            <li>flores</li>
          </ul>
        </div>
        <div
          className={`plano-gold ${selectedPlan === "gold" ? "selected" : ""}`}
          onClick={() => handlePlanClick("gold")}
        >
          <h2>Plano Gold</h2>
          <h3>R$ 80,00</h3>
          <ul>
            <li>mensagem</li>
            <li>foto</li>
            <li>flores</li>
            <li>catavento</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default ContratacaoPlanos;
