import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080'; // Replace with your backend URL

function api() {
    const apiCall = axios.create({
        baseURL: API_BASE_URL,
        headers: {
            'Content-Type': 'application/json',
        },
    });

    const loginPost = async (username, password) => {
        console.log("entrou no loginpost");
        try {
            const response = await apiCall.post('/login', { username, password });
            // Handle successful login response here, e.g., store authentication token
            return response.data;
        } catch (error) {
            // Handle error response here, e.g., display error message
            throw new Error('Login failed. Please try again.');
        }
    };
}


export default api;
