import React, { useState } from 'react';
import '../Styles/contratacao-planos.css';

function ContratacaoPlanos() {
  /*const [planoSelecionado, setPlanoSelecionado] = useState(null);
  const [precoPlano, setPrecoPlano] = useState(0);

  function handleSelecionarPlano(plano, preco) {
    setPlanoSelecionado(plano);
    setPrecoPlano(preco);
  }

  return (
    <div className="container">
      <h1>Conheça nossos planos de personalização</h1>
      <div className="planos-container">
        <div
          className={`plano-basic ${
            planoSelecionado === 'basic' ? 'selecionado' : ''
          }`}
          onClick={() => handleSelecionarPlano('basic', 1)}
        >
          <h2>Plano Basic</h2>
          <p>Inclui mensagem e foto</p>
        </div>
        <div
          className={`plano-silver ${
            planoSelecionado === 'silver' ? 'selecionado' : ''
          }`}
          onClick={() => handleSelecionarPlano('silver', 50)}
        >
          <h2>Plano Silver</h2>
          <p>Inclui mensagem, foto e flores</p>
        </div>
        <div
          className={`plano-gold ${
            planoSelecionado === 'gold' ? 'selecionado' : ''
          }`}
          onClick={() => handleSelecionarPlano('gold', 80)}
        >
          <h2>Plano Gold</h2>
          <p>Inclui mensagem, foto, flores e catavento</p>
        </div>
      </div>
      {planoSelecionado && (
        <div className="checkout-container">
          <h3>Checkout</h3>
          <p>Plano selecionado: {planoSelecionado}</p>
          <p>Preço: R$ {precoPlano},00</p>
          <button>Finalizar compra</button>
        </div>
      )}
    </div>
  );*/
  const [selected, setSelected] = useState(null);
  const [planos] = useState([
    { id: 1, nome: 'Basic', preco: 1, mensagem: true, foto: true, flores: false, catavento: false },
    { id: 2, nome: 'Silver', preco: 50, mensagem: true, foto: true, flores: true, catavento: false },
    { id: 3, nome: 'Gold', preco: 80, mensagem: true, foto: true, flores: true, catavento: true },
  ]);

  const handleClick = (id) => {
    setSelected(id);
  };

  return (
    <div className="planos-container">
      <h1>Conheça nossos planos de personalização</h1>
      <div className="planos-wrapper">
        {planos.map((plano) => (
          <div
            key={plano.id}
            className={`plano-card ${selected === plano.id ? 'selected' : ''}`}
            onClick={() => handleClick(plano.id)}
          >
            <h2>{plano.nome}</h2>
            <h3>R${plano.preco}</h3>
            <ul>
              <li>{plano.mensagem ? 'Mensagem' : 'Sem mensagem'}</li>
              <li>{plano.foto ? 'Foto' : 'Sem foto'}</li>
              <li>{plano.flores ? 'Flores' : 'Sem flores'}</li>
              <li>{plano.catavento ? 'Catavento' : 'Sem catavento'}</li>
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ContratacaoPlanos;
